import { connect } from "react-redux";
import axios from "axios";
import React,{useEffect,Fragment,useState} from 'react'
import {getUserResult, resetUserResult,startBattle} from "../../actions/battle"
import "./Battle.css"
const Battle = ({battle_result,getUserResult, resetUserResult ,startBattle}) => { 
    const [formData,setformData]=useState({
        user1:'',
        user2:''
      });
      const{user1,user2}=formData;
      const onChange=(e)=>{
       setformData({...formData,[e.target.name]:e.target.value}); 
      }
      function isBlank(str) {
        return (!str || /^\s*$/.test(str));
    }
      const onSubmit=async (e)=>{
        e.preventDefault();
        if(!isBlank(user1)&&!isBlank(user2))
       getUserResult(user1,user2);
      }
      const onReset=async()=>{
        setformData({...formData,['user1']:'',['user2']:''}); 
        resetUserResult();
      }
      const battleStart=async()=>{
        startBattle();
      }
      if(battle_result.battle==1){
        let user1_count=0;
        let user2_count=0;
        let user1_result="";
        let user2_result="";
        parseInt(battle_result.user1.followers)>parseInt(battle_result.user2.followers)?user1_count++:user2_count++;
        parseInt(battle_result.user1.following)>parseInt(battle_result.user2.following)?user1_count++:user2_count++;
        parseInt(battle_result.user1.public_repos)>parseInt(battle_result.user2.public_repos)?user1_count++:user2_count++;
        if(user1_count>user2_count){user1_result="Winner";user2_result="Looser"}else{user1_result="Looser";user2_result="Winner"};
return(
<Fragment> <div className="container" style={{marginLeft:'25%'}} >
<div className="col-lg-4 ">
<p className="grid_box">
<p style={{fontSize:'20px',margin:'4px', fontWeight:'500', color:(user1_count>user2_count?'Green':'#bf3838')}}>{user1_result}</p>
<img src={battle_result.user1.avatar_url} />
<p style={{fontSize:'20px',margin:'4px', fontWeight:'500', color:'#bf3838'}}>{battle_result.user1.login}</p>
<p style={{fontSize:'15px',marginTop:'14px', color:'#000', textAlign:'left'}}>{battle_result.user1.login}</p>
<p style={{fontSize:'15px',marginTop:'14px',fontWeight:'300', color:'#000', textAlign:'left'}}>{battle_result.user1.following} Following</p>
<p style={{fontSize:'15px',marginTop:'14px', fontWeight:'300',color:'#000', textAlign:'left'}}>{battle_result.user1.followers} Followers</p>
<p style={{fontSize:'15px',marginTop:'14px', fontWeight:'300',color:'#000', textAlign:'left'}}>{battle_result.user1.public_repos} repositories</p>        
</p>
</div>
<div className="col-lg-4 ">
<p className="grid_box">
<p style={{fontSize:'20px',margin:'4px', fontWeight:'500', color:(user2_count>user1_count?'Green':'#bf3838')}}>{user2_result}</p>
<img src={battle_result.user2.avatar_url} />
<p style={{fontSize:'20px',margin:'4px', fontWeight:'500', color:'#bf3838'}}>{battle_result.user2.login}</p>
<p style={{fontSize:'15px',marginTop:'14px', color:'#000', textAlign:'left'}}>{battle_result.user2.login}</p>
<p style={{fontSize:'15px',marginTop:'14px',fontWeight:'300', color:'#000', textAlign:'left'}}>{battle_result.user2.following} Following</p>
<p style={{fontSize:'15px',marginTop:'14px',fontWeight:'300', color:'#000', textAlign:'left'}}>{battle_result.user2.followers} Followers</p>
<p style={{fontSize:'15px',marginTop:'14px',fontWeight:'300', color:'#000', textAlign:'left'}}>{battle_result.user2.public_repos} repositories</p>        
</p>
</div>
<section className="container" style={{clear:'left',marginLeft:'30%',textAlign:'left'}}>
<button className="btn btn-primary"  style={{backgroundColor:'#000'}} onClick={()=>onReset()}> Reset</button>
</section>
  </div>
  </Fragment>
);
      }
      if(battle_result.user1!==null && battle_result.user2!=null){

        return(<Fragment> <div class="container-fluid">
            <div className="row">
<div className="column">
  Player One
</div>
<div className="col-sm-6">
Player Two
</div>
</div>
<div className="row">
<div className="col-sm-6">
<p className="box">
<img src={battle_result.user1.avatar_url} />
{battle_result.user1.login}
</p>
</div>
<div className="col-sm-6 ">
<p className="box">
<img src={battle_result.user2.avatar_url} />
{battle_result.user2.login}
</p>
</div>
</div>
<div className="row">
<div className="col-sm-12 " style={{textAlign:'center'}}>
<button className="btn btn-primary"  style={{backgroundColor:'#000'}} onClick={()=>battleStart()}> BATTLE</button>
</div>
</div>
  </div>
  </Fragment>
        );
      }
      else{
      return (
        <div>
        <h1 className="large text-primary">Battle Page</h1>


         <form className="column" onSubmit={e=>onSubmit(e)} >
         <label className="header" >
          Player one
        </label>
            <input type="text" placeholder="User 1" name="user1" value={user1} onChange={e=>onChange(e)} />

         <label className="header" >
          Player two
        </label> 
          <input type="text" placeholder="User 2" name="user2" value={user2} onChange={e=>onChange(e)} />



          <input type="submit" className="button" value="Search" />
        </form>
        </div>

      );
      }
};


const mapStateToProps = state => ({
  battle_result: state.battle,
});

export default connect(mapStateToProps,{getUserResult, resetUserResult,startBattle} )(Battle);
