import { connect } from "react-redux";
import Cities from "./Cities";
import axios from "axios";

const getFilterCities = (data, filter) => {
  let lang_result = [];
  let baseUrl="https://api.github.com/search/repositories?q=stars:%3E1+language:"+(filter=="All"?'':filter)+"&sort=stars&order=desc&type=Repositories";
    axios.get(baseURL).then((response) => {
      response.data.items.forEach(item => {
        lang_result .push(item.lang);

      });
    });

  return lang_result;
};

const mapStateToProps = state => ({
  lang_result: getFilterCities(state.data, state.visibilityFilter)
});

export default connect(mapStateToProps)(Cities);
