import { connect } from "react-redux";
import axios from "axios";
import React,{useEffect,Fragment,useState} from 'react'
import {getUserResult, resetUserResult} from "../../actions/battle"
const Battle = ({battle_result,getUserResult, resetUserResult }) => { 
    const [formData,setformData]=useState({
        user1:'',
        user2:''
      });
      const{user1,user2}=formData;
      const onChange=(e)=>{
       setformData({...formData,[e.target.name]:e.target.value}); 
      }
      const onSubmit=async (e)=>{
        e.preventDefault();
       getUserResult(user1,user2);
      }
      if(battle_result.user1!==null && battle_result.user2!=null){

      }
      else{
      return (
        <section className="container">
        <h1 className="large text-primary">Battle Page</h1>
        <p className="lead"><i className="fas fa-user"></i> Enter The Details To Start Battle</p>
        <form className="form" onSubmit={e=>onSubmit(e)} >
          <div className="form-group">
            <input type="text" placeholder="User 1" name="user1" value={user1} onChange={e=>onChange(e)} />

          </div>
          <div className="form-group">
          <input type="text" placeholder="User 2" name="user2" value={user2} onChange={e=>onChange(e)} />

          </div>

          <input type="submit" className="btn btn-primary" value="Submit" />
        </form>
        </section>

      );
      }
};


const mapStateToProps = state => ({
  battle_result: state.battle,
});

export default connect(mapStateToProps,{getUserResult, resetUserResult} )(Battle);
