import { connect } from "react-redux";
import axios from "axios";
import React,{useEffect,Fragment} from 'react'
import {getLangs} from "../../actions/langs"
import "./Cities.css";
const Cities = ({getLangs, lang_result,filter }) => {
  const {lang,click}=filter;
  console.log(lang);
 if(click==1)
  getLangs(lang);

      return (lang_result.lang_obj==null?'':(<Fragment> <ul className="popular-list">

        {lang_result.lang_obj.items.map(item => <div className="col-sm-6 col-md-4 col-lg-3 "><p className="grid_box">

          <img src={item.owner.avatar_url} />
          <p style={{fontSize:'20px',margin:'4px', fontWeight:'500', color:'#bf3838'}}>{item.owner.login}</p>
          <p style={{fontSize:'15px',marginTop:'14px', color:'#000', textAlign:'left'}}>{item.owner.login}</p>
          <p style={{fontSize:'15px',marginTop:'14px', fontWeight:'300', color:'#000', textAlign:'left'}}>{item.stargazers_count} stars</p>
          <p style={{fontSize:'15px',marginTop:'14px', fontWeight:'300', color:'#000', textAlign:'left'}}>{item.forks_count} forks</p>
          <p style={{fontSize:'15px',marginTop:'14px', fontWeight:'300', color:'#000', textAlign:'left'}}>{item.open_issues} open issues</p>

          </p></div>

        )}
     </ul></Fragment>));
};


const mapStateToProps = state => ({
  lang_result: state.lang,
  filter:state.visibilityFilter
});

export default connect(mapStateToProps,{getLangs} )(Cities);
