import { connect } from "react-redux";
import axios from "axios";
import React,{useEffect,Fragment,useState} from 'react'
import {getUserResult, resetUserResult} from "../../actions/battle"
const Battle = ({battle_result,getUserResult, resetUserResult }) => { 
    const [formData,setformData]=useState({
        user1:'',
        user2:''
      });
      const{user1,user2}=formData;
      const onChange=(e)=>{
       setformData({...formData,[e.target.name]:e.target.value}); 
      }
      function isBlank(str) {
        return (!str || /^\s*$/.test(str));
    }
      const onSubmit=async (e)=>{
        e.preventDefault();
        if(!isBlank(user1)&&!isBlank(user2))
       getUserResult(user1,user2);
      }
      if(battle_result.user1!==null && battle_result.user2!=null){
        let user1_count=0;
        let user2_count=0;
        let user1_result="";
        let user2_result="";
        parseInt(battle_result.user1.followers)>parseInt(battle_result.user2.followers)?user1_count++:user2_count++;
        parseInt(battle_result.user1.following)>parseInt(battle_result.user2.following)?user1_count++:user2_count++;
        parseInt(battle_result.user1.public_repos)>parseInt(battle_result.user2.public_repos)?user1_count++:user2_count++;
        if(user1_count>user2_count){user1_result="Winner";user2_result="Looser"}else{user1_result="Looser";user2_result="Winner"};
return(
<Fragment> <div className="container" style={{marginLeft:'25%'}} >
<div className="col-lg-4 ">
<p className="grid_box">
<p style={{fontSize:'20px',margin:'4px', fontWeight:'500', color:(user1_count>user2_count?'Green':'#bf3838')}}>{user1_result}</p>
<img src={battle_result.user1.avatar_url} />
<p style={{fontSize:'20px',margin:'4px', fontWeight:'500', color:'#bf3838'}}>{battle_result.user1.login}</p>
<p style={{fontSize:'15px',marginTop:'14px', color:'#000', textAlign:'left'}}>{battle_result.user1.following} Following</p>
<p style={{fontSize:'15px',marginTop:'14px', color:'#000', textAlign:'left'}}>{battle_result.user1.followers} Followers</p>
<p style={{fontSize:'15px',marginTop:'14px', color:'#000', textAlign:'left'}}>{battle_result.user1.public_repos} repositories</p>        
</p>
</div>
<div className="col-lg-4 ">
<p className="grid_box">
<p style={{fontSize:'20px',margin:'4px', fontWeight:'500', color:(user2_count>user1_count?'Green':'#bf3838')}}>{user2_result}</p>
<img src={battle_result.user2.avatar_url} />
<p style={{fontSize:'20px',margin:'4px', fontWeight:'500', color:'#bf3838'}}>{battle_result.user2.login}</p>
<p style={{fontSize:'15px',marginTop:'14px', color:'#000', textAlign:'left'}}>{battle_result.user2.following} Following</p>
<p style={{fontSize:'15px',marginTop:'14px', color:'#000', textAlign:'left'}}>{battle_result.user2.followers} Followers</p>
<p style={{fontSize:'15px',marginTop:'14px', color:'#000', textAlign:'left'}}>{battle_result.user2.public_repos} repositories</p>        
</p>

</div>
<section className="container">
<button className="btn btn-primary" > Reset</button>
</section>
  </div>
  </Fragment>
);
      }
      else{
      return (
        <section className="container">
        <h1 className="large text-primary">Battle Page</h1>
        <p className="lead"><i className="fas fa-user"></i> Enter The Details To Start Battle</p>
        <form className="form" onSubmit={e=>onSubmit(e)} >
          <div className="form-group">
            <input type="text" placeholder="User 1" name="user1" value={user1} onChange={e=>onChange(e)} />

          </div>
          <div className="form-group">
          <input type="text" placeholder="User 2" name="user2" value={user2} onChange={e=>onChange(e)} />

          </div>

          <input type="submit" className="btn btn-primary" value="Submit" />
        </form>
        </section>

      );
      }
};


const mapStateToProps = state => ({
  battle_result: state.battle,
});

export default connect(mapStateToProps,{getUserResult, resetUserResult} )(Battle);
